struct SideButton: View {
    var icon: String
    var text: String

    var body: some View {
        VStack {
            Image(systemName: icon)
                .font(.title)
                .foregroundColor(.white)
            Text(text)
                .font(.footnote)
                .foregroundColor(.white)
        }
    }
}

struct TabButton: View {
    var icon: String
    var label: String
    var badgeValue: Int? = nil
    var isPlus: Bool = false

    var body: some View {
        VStack {
            ZStack {
                Image(systemName: icon)
                    .font(isPlus ? .largeTitle : .title2)
                    .foregroundColor(isPlus ? .cyan : .white)

                if let badgeValue = badgeValue, badgeValue > 0 {
                    Text("\(badgeValue)")
                        .font(.caption2)
                        .padding(4)
                        .background(Color.red)
                        .foregroundColor(.white)
                        .clipShape(Circle())
                        .offset(x: 15, y: -10)
                }
            }
            if !isPlus {
                Text(label)
                    .font(.caption)
                    .foregroundColor(.white)
            }
        }
    }
}

